struct arphdr
{
 unsigned short ar_hrd; /* аппаратный адрес */
 unsigned short ar_pro; /* адрес протокола */
 unsigned char ar_hln; /* длна аппаратного адреса */
 unsigned char ar_pln; /* длина адреса протокола */
 unsigned short ar_op; /* ARP-команда */
 unsigned char ar_sha[ ETH_ALEN ]; /* аппаратный адрес отправителя */
