 unsigned char ar_tip[ 4 ]; /* IP получателя */
};


/*--------------------------------------------------*/
/* Функция вывода полей заголовков принятых пакетов */
/*--------------------------------------------------*/
void PrintHeaders( void * data )
{
 struct ethhdr * eth = 0;
 struct iphdr * ip = 0;
 struct arphdr * arp = 0;
 struct tcphdr * tcp = 0;
 struct udphdr * udp = 0;
 struct icmphdr * icmp = 0;
 struct in_addr * tmp_in_addr = 0;

 eth = (struct ethhdr*)malloc( sizeof( struct ethhdr ) );
 ip = (struct iphdr*)malloc( sizeof( struct iphdr ) );
 arp = (struct arphdr*)malloc( sizeof( struct arphdr ) );
 tcp = (struct tcphdr*)malloc( sizeof( struct tcphdr ) );
 udp = (struct udphdr*)malloc( sizeof( struct udphdr ) );
 icmp = (struct icmphdr*)malloc( sizeof( struct icmphdr ) );
 tmp_in_addr = (struct in_addr*)malloc( sizeof( struct in_addr ) );

 if( tmp_in_addr == NULL || eth == NULL || ip == NULL || arp == NULL || tcp == NULL || udp == NULL || icmp == NULL )
 {
 printf( "*******************************************\n" );
 printf( "* SOMETHING WRONG. CANNOT ALLOCATE MEMORY *\n" );
 printf( "* TRY TO DO IT AGAIN *\n" );
 printf( "*******************************************\n" );
 PrintHeaders( data );
 free( eth );
 free( ip );
 free( arp );
 free( tcp );
 free( udp );
 free( icmp );
 free( tmp_in_addr );
 return;
 }

 memcpy( (char*)eth, data, sizeof( struct ethhdr ) );

 printf( "==ETHERNET_HEADER============================\n" );
 printf( "MAC destination ");
 printf( ":%.2x:%.2x:%.2x:%.2x:%.2x:%.2x\n",
 eth->h_source[0], eth->h_source[1], eth->h_source[2],
 eth->h_source[3], eth->h_source[4], eth->h_source[5] );

 printf( "MAC source ");
 printf( ":%.2x:%.2x:%.2x:%.2x:%.2x:%.2x\n",
 eth->h_dest[0], eth->h_dest[1], eth->h_dest[2],
 eth->h_dest[3], eth->h_dest[4], eth->h_dest[5] );
 printf( "Packet type ID field :%#x\n", ntohs( eth->h_proto ) );

 if( ntohs(eth->h_proto) == ETH_P_ARP || ntohs(eth->h_proto) == ETH_P_RARP )
 {
 arp = (struct arphdr*)( data + sizeof( struct ethhdr ) );

 printf( "==ARP_HEADER=================================\n" );
 printf( "Format of hardware address :%d\n", htons( arp->ar_hrd ) );
 printf( "Format of protocol address :%d\n", arp->ar_pro );
 printf( "Length MAC :%d\n", arp->ar_hln );
 printf( "Length IP :%d\n", arp->ar_pln );
 printf( "ARP opcode :%d\n", htons( arp->ar_op ) );
 printf( "Sender hardware address :%.2x:%.2x:%.2x:%.2x:%.2x:%.2x\n",
 arp->ar_sha[0],
 arp->ar_sha[1],
 arp->ar_sha[2],
 arp->ar_sha[3],
 arp->ar_sha[4],
 arp->ar_sha[5] );
 printf( "Sender IP address :%d.%d.%d.%d\n",
 arp->ar_sip[0],
 arp->ar_sip[1],
 arp->ar_sip[2],
 arp->ar_sip[3] );
 printf( "Target hardware address :%.2x:%.2x:%.2x:%.2x:%.2x:%.2x\n",
 arp->ar_tha[0],
 arp->ar_tha[1],
 arp->ar_tha[2],
 arp->ar_tha[3],
 arp->ar_tha[4],
 arp->ar_tha[5] );
 printf( "Target IP address :%d.%d.%d.%d\n",
 arp->ar_tip[0],
 arp->ar_tip[1],
 arp->ar_tip[2],
 arp->ar_tip[3] );

 printf( "#############################################\n" );

 }

 if( ntohs( eth->h_proto ) == ETH_P_IP )
 {
 ip = (struct iphdr*)( data + sizeof( struct ethhdr ) );

 printf( "==IP_HEADER==================================\n" );
 printf( "IP version :%d\n", ip->version );
 printf( "IP header length :%d\n", ip->ihl );
 printf( "TOS :%d\n", ip->tos );
 printf( "Total length :%d\n", ntohs( ip->tot_len ) );
 printf( "ID :%d\n", ntohs( ip->id ) );
 printf( "Fragment offset :%#x\n", ntohs( ip->frag_off ) );
 printf( "MF :%d\n", ntohs( ip->frag_off ) & IP_MF ? 1 : 0 );
 printf( "DF :%d\n", ntohs( ip->frag_off ) & IP_DF ? 1 : 0 );
 printf( "TTL :%d\n", ip->ttl );
 printf( "Protocol :%d\n", ip->protocol );

 tmp_in_addr->s_addr = ip->saddr;
 printf( "IP source :%s\n", inet_ntoa( *tmp_in_addr ) );
 tmp_in_addr->s_addr = ip->daddr;
 printf( "IP destination :%s\n", inet_ntoa( *tmp_in_addr ) );

 if( ip->protocol == IPPROTO_TCP )
 {
 tcp = (struct tcphdr*)( data + sizeof( struct ethhdr ) + sizeof( struct iphdr ) );
 printf( "==TCP_HEADER=================================\n" );
 printf( "Port source :%d\n", ntohs( tcp->source ) );
 printf( "Port destination :%d\n", ntohs( tcp->dest ) );
 printf( "Sequence number :%d\n", ntohs( tcp->seq ) );
 printf( "Ack number :%d\n", ntohs( tcp->ack_seq ) );
 printf( "Data offset :%d\n", tcp->doff );
 printf( "FIN:%d,", tcp->fin);
 printf( "SYN:%d,", tcp->syn);
 printf( "RST:%d,", tcp->rst);
 printf( "PSH:%d,", tcp->psh);
 printf( "ACK:%d,", tcp->ack);
 printf( "URG:%d,", tcp->urg);
 printf( "ECE:%d,", tcp->ece);
 printf( "CWR:%d\n", tcp->cwr);
 printf( "Window :%d\n", ntohs( tcp->window ) );
 printf( "Urgent pointer :%d\n", tcp->urg_ptr );
 }

 if( ip->protocol == IPPROTO_UDP )
 {
 udp = (struct udphdr*)( data + sizeof( struct ethhdr ) + sizeof( struct iphdr ) );
 printf( "==UDP_HEADER=================================\n" );
 printf( "Port source :%d\n", ntohs( udp->source ) );
 printf( "Port destination :%d\n", ntohs( udp->dest ) );
 printf( "Length :%d\n", ntohs( udp->len ) );
 }

 if( ip->protocol == IPPROTO_ICMP )
 {
 icmp = (struct icmphdr*)( data + sizeof( struct ethhdr ) + sizeof( struct iphdr ) );
 printf( "==ICMP_HEADER================================\n" );
 printf( "Type :%d\n", icmp->type );
 printf( "Code :%d\n", icmp->code );
 }

 printf( "#############################################\n" );
 }

 free( eth );
 free( ip );
 free( arp );
 free( tcp );
 free( udp );
 free( icmp );
 free( tmp_in_addr );
}

/*-------------------------------------------------*/
/* Функция вывода принятых данных в виде Intel HEX */
/*-------------------------------------------------*/
void PrintData( void * data, int len )
{
 unsigned char * buf = (unsigned char*)data;
 int i;
 int poz = 0;
 char str[ 17 ];
 memset( str, 0, 17) ;
 for( i = 0; i < len; i++ )
 {
 if( poz % 16 == 0 )
 {
 printf( " %s\n%04X: ", str, poz );
 memset( str, 0, 17 );
 }

 if( buf[ poz ] < ' ' || buf[ poz ] >= 127)
 str[ poz % 16] = '.';
 else
 str[ poz % 16 ] = buf[ poz ];

 printf( "%02X ", buf[ poz++ ] );
 }
 printf( " %*s\n\n", 16 + ( 16 - len % 16 ) * 2, str );
}

/*------------------------*/
int main( /*int argc, char* argv[]*/ )
{
 int sd;
 int n = 0;
 int packet = 0;
 struct ifreq ifr;
 char buf[ 1500 ];

 printf( "======================\n" );
 printf( "= Try to open socket =\n" );
 printf( "======================\n" );

 sd = socket( PF_PACKET, SOCK_RAW, htons( ETH_P_ALL ) );
 if( sd < 0 )
 {
 perror( "socket() failed" );
 return(-1);
 }

 printf( "!!! Socket opened\n" );

 /* переводим интерфейс в неразборчивый (promiscuous) режим */
 strcpy( ifr.ifr_name, DEVICE );
 if( ioctl( sd, SIOCGIFFLAGS, &ifr ) < 0 )
 {
 perror( "ioctl() failed" );
 close( sd );
 return(-1);
 }

 printf( "!!! Data of %s interface was saved to buffer\n", DEVICE );

 ifr.ifr_flags |= IFF_PROMISC;

 if( ioctl( sd, SIOCSIFFLAGS, &ifr ) < 0 )
 {
 perror( "ioctl() failed" );
 close( sd );
 return(-1);
 }

 printf( "!!! The %s interface was switched to promiscuous mode\n", DEVICE );

 /* в бесконечном цикле захватываем пакеты */
 while( 1 )
 {
 n = recvfrom( sd, buf, sizeof( buf ), 0, 0, 0 );
 printf( "#############################################\n" );
 printf( "Packet #%d (%d bytes read)\n", ++packet, n );

 /* выводим поля заголовков принятых пакетов */
 PrintHeaders( buf );

 /* показываем принятые данные в виде Intel HEX */
 PrintData( buf, n );

 printf("\n");
 }

 return 0;
}

